package com.example.studentslistadvanced.db

import com.example.studentslistadvanced.model.Student
import java.util.*
import kotlin.collections.HashMap

const val SIZE = 5
const val IMAGE = "data:image/jpeg;base64,"

class SimpleDB {

    private var id = 0
    private val list = HashMap<String, Student>()
    private val deletedList: LinkedList<Student> = LinkedList()

    companion object {
        @Volatile
        private var INSTANCE: SimpleDB? = null

        fun getInstance(): SimpleDB {
            if (INSTANCE == null) {
                synchronized(this) {
                    INSTANCE = SimpleDB()
                }
            }
            return INSTANCE!!
        }
    }

    fun getId(): Int = id

    fun getRestoreListSize(): Int = deletedList.size

    fun incrementId() = ++id

    fun getList(): HashMap<String, Student> {
        return list
    }

    fun containsStudent(name: String): Boolean {
        return list.containsKey(name)
    }

    fun addStudent(name: String, student: Student) {
        list.put(name, student)
    }

    fun deleteStudent( student: Student) {
        list.remove(student.firstName)
        addToDeletedList(student)
    }

    private fun addToDeletedList(student: Student) {
        if (deletedList.size < SIZE) {
            deletedList.add(student)
        } else {
            deletedList.removeFirst()
            deletedList.add(student)
        }
    }

    fun restoreStudent(): Boolean {
        if (deletedList.isEmpty()) return false
        val student = deletedList.removeLast()
        student?.let {
            addStudent(student.firstName, student)
            return true
        }
        return false
    }
}